<p>{this.state.user.bio}</p>
{this.state.followers.map(i => <p>{i.login}</p>)}

  </div>
 <Name name={this.state.name} />
 <form>
   <input 
   onChange={this.handleChange}
   value={this.state.name}
   />
   <button onClick={this.fetchUser}>Search</button>
   </form>
 </div>
);


}





}

